import { ethers } from 'ethers';
import { useEffect, useState } from 'react';
import { useRef } from 'react';

import close from '../assets/close.svg';

const Candidate = ({ candidates, provider, account, handleSubmit , addCandidate, togglePop }) => {
    const candidateNameRef = useRef();
    const candidateAddressRef = useRef();

	return (

		<div className="home">
            <div className='home__details'>
                <div className="home__image">

					<div className="pop-up">
						<form onSubmit={handleSubmit}>
						    <input
						          ref={candidateNameRef}
						          type="text"
						          placeholder="Enter candidate name"
						          required
						    />

						        <input
						          ref={candidateAddressRef}
						          type="text"
						          placeholder="Enter candidate address"
						          required
						        />

						        <button type="submit">Add Candidate</button>
						        <button onClick={togglePop}>Close</button>
						      </form>
						    </div>


                </div>




             	<button onClick={togglePop} className="home__close">
                	<img src={close} alt="Close" />
            	</button>
            </div>


		</div>
	);

};
export default Candidate;
