import { useEffect, useState } from 'react'
import { ethers } from 'ethers'

// Components
import Navigation from './components/Navigation'
import Section from './components/Section'
import Candidates from './components/Candidates'

// ABIs
import Voting from './abis/Voting.json'

// Config
import config from './config.json'

function App() {

  const [account, setAccount] = useState(null)
  const [providers, setProvider] = useState(null)

  const [voting, setVoting] = useState(null)
  const [candidates, setCandidate] = useState(null)

  const loadBlockchainData = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    setProvider(provider)

    const network = await provider.getNetwork()
    const voting = new ethers.Contract(config[network.chainId].Voting.address, Voting, provider)
    setVoting(voting)


    const candidateCount = await voting.candidates.length()
    const candidateAddresses = await voting.getAllCandidates()
    const candidateList = []
    for (let i = 0; i < candidateCount; i++) {
      const candidate = await voting.getCandidate(i)
      candidateList.push(candidate)
    }
    setCandidate(candidateList)    




    window.ethereum.on('accountsChanged', async () => {
      window.location.reload()
    })  
  };


  useEffect(() => {
    loadBlockchainData()
  }, [])

  return (
    <div>
      <Navigation account = {account} setAccount = {setAccount} />

        <div className="grid grid-cols-3 gap-4">
          {candidates.map((candidate, index) => (
            <Candidates
              key={index}
              title={candidate[1]}
              description={`ID: ${candidate[2]} Vote Count: ${candidate[3]}`}
            />
          ))}
        </div>
    </div>
  );
}

export default App;
