import { useEffect, useState } from 'react'
import { ethers } from 'ethers'

// Components
import Navigation from './components/Navigation'
import Candidates from './components/Candidates'

// ABIs
import Voting from './abis/Voting.json'

// Config
import config from './config.json'

function App() {

  const [account, setAccount] = useState(null)
  const [providers, setProvider] = useState(null)

  const [voting, setVoting] = useState(null)
  const [candidates, setCandidate] = useState(null)





  const loadBlockchainData = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    setProvider(provider)

    const network = await provider.getNetwork()
    const voting = new ethers.Contract(config[network.chainId].Voting.address, Voting, provider)
    setVoting(voting) 

    window.ethereum.on('accountsChanged', async () => {
      window.location.reload()
    })  
  };


  const addCandidate = async (candidateName) => {
    if (!account) {
      alert('Please connect your account first');
      return;
    }

    if (!voting) {
      alert('Voting contract not loaded');
      return;
    }

    try {
      const signer = provider.getSigner();
      const votingWithSigner = voting.connect(signer);
      const candidateId = candidates.length;

      const tx = await votingWithSigner.addCandidate(account, candidateName, candidateId);
      await tx.wait();

      alert('Candidate added successfully');
      window.location.reload();
    } catch (error) {
      console.error('Error adding candidate:', error);
      alert('Error adding candidate. Please try again.');
    }
  };

  useEffect(() => {
    loadBlockchainData()
  }, [])

  return (
    <div>
      <Navigation account = {account} setAccount = {setAccount} />

        <div className="grid grid-cols-3 gap-4">
            <Candidates candidates = {candidates} provider = {providers} account = {account} voting = {voting} onAddCandidate={addCandidate}/>
        </div>
    </div>
  );
}

export default App;
