import { useEffect, useState } from 'react'
import { ethers } from 'ethers'
import { useRef } from 'react';

const Candidates = ({ candidates, provider, account, voting , onAddCandidate}) => {
	const [fetchedCandidates, setFetchedCandidates] = useState([]);


	// Inside Candidates.js
	const handleSubmit = async (event) => {
	  event.preventDefault();
	  const candidateName = candidateNameRef.current.value.trim();
	  const candidateAddress = candidateAddressRef.current.value.trim();

	  if (candidateName) {
	    await onAddCandidate(candidateAddress, candidateName, fetchedCandidates.length);
	    candidateNameRef.current.value = '';
	    togglePop();
	  }
	};


	const fetchCandidates = async () => {
	if (!voting) return;

	try {
	  const [candidateAddresses, candidateNames, candidateIds, candidateVoteCounts] = await voting.getAllCandidates();
	  const candidateList = [];

	  for (let i = 0; i < candidateIds.length; i++) {
	    candidateList.push({
	      id: candidateIds[i].toNumber(),
	      name: candidateNames[i],
	      address: candidateAddresses[i],
	      voteCount: candidateVoteCounts[i].toNumber()
	    });
	  }

	  setFetchedCandidates(candidateList);
	} catch (error) {
	  console.error("Error fetching candidates:", error);
	}
	};

	useEffect(() => {
		fetchCandidates();
	}, [voting]);


	return (
	<div className="message.content">
		<h1>List of Candidates</h1>
		<ul>
			{fetchedCandidates.map((candidate) => (
			  <li key={candidate.id}>
			    {candidate.name} ({candidate.voteCount} votes)
			  </li>
			))}
		</ul>
	</div>
	);
};

export default Candidates;
