import { useEffect, useState } from 'react'
import { ethers } from 'ethers'

// Components
import Navigation from './components/Navigation'
import Candidates from './components/Candidates'

// ABIs
import Voting from './abis/Voting.json'

// Config
import config from './config.json'

function App() {

  const [account, setAccount] = useState(null)
  const [providers, setProvider] = useState(null)

  const [voting, setVoting] = useState(null)
  const [candidates, setCandidate] = useState(null)

  const [contractOwner, setContractOwner] = useState(null);




  const loadBlockchainData = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    setProvider(provider);
    console.log("Provider:", provider);

    const network = await provider.getNetwork();
    console.log("Network:", network);
    console.log("Voting contract address:", config[network.chainId].Voting.address);

    const voting = new ethers.Contract(config[network.chainId].Voting.address, Voting.abi, provider);
    setVoting(voting);
    console.log("Voting Contract:", voting);

    try {
      const contractOwner = await voting.owner();
      setContractOwner(contractOwner);
    } catch (error) {
      console.error('Error fetching contract owner:', error);
      alert('Error fetching contract owner. Please make sure the contract is deployed correctly.');
    }

    const [candidateAddresses, candidateNames, candidateIds, candidateVoteCounts] = await voting.getAllCandidates();
    const candidateArray = candidateAddresses.map((candidateAddress, index) => {
      return {
        address: candidateAddress,
        name: candidateNames[index],
        id: candidateIds[index],
        voteCount: candidateVoteCounts[index],
      };
    });
    setCandidate(candidateArray);

    window.ethereum.on('accountsChanged', async () => {
      window.location.reload();
    });
  };



  const addCandidate = async (candidateName, candidateId) => {


    if (!account) {
      alert('Please connect your account first');
      return;
    }

    if (!voting) {
      alert('Voting contract not loaded');
      return;
    }

    if (account !== contractOwner) {
      alert('Only the contract owner can add candidates');
      return;
    }

    try {
      const signer = providers.getSigner();
      const votingWithSigner = voting.connect(signer);
      const nonce = await providers.getTransactionCount(account);


      const tx = await votingWithSigner.addCandidate(account, candidateName, candidateId, { nonce });
      await tx.wait();

      alert('Candidate added successfully');
      window.location.reload();
    } catch (error) {
      console.error('Error adding candidate:', error);
      alert('Error adding candidate. Please try again.');
    }
  };

  useEffect(() => {
    loadBlockchainData()
  }, [])

  return (
    <div>
      <Navigation account = {account} setAccount = {setAccount} />

        <div className="grid grid-cols-3 gap-4">
            <Candidates candidates = {candidates} provider = {providers} account = {account} voting = {voting} onAddCandidate={addCandidate}/>
        </div>
    </div>
  );
}

export default App;
