import { ethers } from 'ethers';
import { useEffect, useState } from 'react';

import close from '../assets/close.svg';

const Candidate = ({ candidates, provider, account, voting , addCandidate, togglePop }) => {

	const candidateNameRef = useRef();
	const candidateAddressRef = useRef();


	const handleSubmit = async (event) => {
      event.preventDefault();
      const candidateName = candidateNameRef.current.value.trim();
      const candidateAddress = candidateAddressRef.current.value.trim();

      if (candidateName) {
        await addCandidate(candidateAddress, candidateName, Candidates.length);
        candidateNameRef.current.value = '';
        togglePop();
      }
    };

	return (

	  <div className='card' key={index} onClick={() => togglePop(candidate)}>
		  {toggle && (
		    <div className="pop-up">
		      <form onSubmit={handleSubmit}>
		        <input
		          ref={candidateNameRef}
		          type="text"
		          placeholder="Enter candidate name"
		          required
		        />

		        <input
		          ref={candidateAddressRef}
		          type="text"
		          placeholder="Enter candidate address"
		          required
		        />

		        <button type="submit">Add Candidate</button>
		      </form>
		    </div>
		  )}
	  </div>
	);

}
